contracts/Global/HeliosGlobals.sol.content:
// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.0;

contract HeliosGlobals {
    address public globalAdmin;
    address public governor;
    bool    public protocolPaused;

    mapping(address => bool) public isValidPoolDelegate;
    mapping(address => bool) public isValidPoolFactory;

    event ProtocolPaused(bool pause);
    event Initialized();
    event GlobalAdminSet(address indexed newGlobalAdmin);
    event PoolDelegateSet(address indexed delegate, bool valid);

    modifier isGovernor() {
        require(msg.sender == governor, "MG:NOT_GOV");
        _;
    }

    constructor(address _governor, address _globalAdmin) {
        governor = _governor;
        globalAdmin = _globalAdmin;
        emit Initialized();
    }

    function setGlobalAdmin(address newGlobalAdmin) external {
        require(msg.sender == governor && newGlobalAdmin != address(0), "HG:NOT_GOV_OR_ADMIN");
        require(!protocolPaused, "HG:PROTO_PAUSED");
        globalAdmin = newGlobalAdmin;
        emit GlobalAdminSet(newGlobalAdmin);
    }

    function setProtocolPause(bool pause) external {
        require(msg.sender == globalAdmin, "HG:NOT_ADMIN");
        protocolPaused = pause;
        emit ProtocolPaused(pause);
    }

    function setValidPoolFactory(address poolFactory, bool valid) external isGovernor {
        isValidPoolFactory[poolFactory] = valid;
    }

    function setPoolDelegateAllowList(address delegate, bool valid) external isGovernor {
        isValidPoolDelegate[delegate] = valid;
        emit PoolDelegateSet(delegate, valid);
    }
}

contracts/Pool/Pool.sol.content:
// SPDX-License-Identifier: Unlicensed

pragma solidity ^0.8.0;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "../Global/HeliosGlobals.sol";
import "./PoolFactory.sol";

contract Pool is ERC20 {
    address public immutable superFactory;
    address public poolDelegate;
    uint256 public lockupPeriod;
    uint256 public apy;
    uint256 public maxInvestmentSize;
    uint256 public minInvestmentSize;

    enum State {Initialized, Finalized, Deactivated}
    State public poolState;

    event PoolStateChanged(State state);
    event PoolAdminSet(address indexed poolAdmin, bool allowed);

    mapping(address => bool)public poolAdmins;

    constructor(
        address _poolDelegate,
        uint256 _lockupPeriod,
        uint256 _apy,
        uint256 _maxInvestmentSize,
        uint256 _minInvestmentSize,
        string memory name,
        string memory symbol
    ) ERC20(name, symbol){
        superFactory = msg.sender;
        poolDelegate = _poolDelegate;
        lockupPeriod = _lockupPeriod;
        apy = _apy;
        maxInvestmentSize = _maxInvestmentSize;
        minInvestmentSize = _minInvestmentSize;
        poolState = State.Initialized;
        emit PoolStateChanged(poolState);
    }

    function finalize() external {
        _isValidDelegateAndProtocolNotPaused();
        _isValidState(State.Initialized);
        poolState = State.Finalized;
        emit PoolStateChanged(poolState);
    }

    function deactivate() external {
        _isValidDelegateAndProtocolNotPaused();
        _isValidState(State.Finalized);
        poolState = State.Deactivated;
        emit PoolStateChanged(poolState);
    }

    function setPoolAdmin(address poolAdmin, bool allowed) external {
        _isValidDelegateAndProtocolNotPaused();
        poolAdmins[poolAdmin] = allowed;
        emit PoolAdminSet(poolAdmin, allowed);
    }

    function _isValidState(State _state) internal view {
        require(poolState == _state, "P:BAD_STATE");
    }

    function _isValidDelegate() internal view {
        require(msg.sender == poolDelegate, "P:NOT_DEL");
    }

    function _globals(address poolFactory) internal view returns (HeliosGlobals) {
        return HeliosGlobals(PoolFactory(poolFactory).globals());
    }

    function _isValidDelegateOrPoolAdmin() internal view {
        require(msg.sender == poolDelegate || poolAdmins[msg.sender], "P:NOT_DEL_OR_ADMIN");
    }

    function _whenProtocolNotPaused() internal view {
        require(!_globals(superFactory).protocolPaused(), "P:PROTO_PAUSED");
    }

    function _isValidDelegateAndProtocolNotPaused() internal view {
        _isValidDelegate();
        _whenProtocolNotPaused();
    }
}

contracts/Pool/PoolFactory.sol.content:
// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.0;

import {Pausable} from "@openzeppelin/contracts/security/Pausable.sol";
import "./Pool.sol";
import "../Global/HeliosGlobals.sol";

contract PoolFactory is Pausable {
    uint256         public poolsCreated;
    HeliosGlobals   public globals;

    mapping(uint256 => address) public pools;              // Map to reference Pools corresponding to their respective indices.
    mapping(address => bool)    public isPool;             // True only if a Pool was instantiated by this factory.
    mapping(address => bool)    public poolFactoryAdmins;  // The PoolFactory Admin addresses that have permission to do certain operations in case of disaster management.

    event PoolFactoryAdminSet(address indexed poolFactoryAdmin, bool allowed);

    event PoolCreated(address indexed pool, address indexed delegate, string name, string symbol);

    constructor(address _globals) {
        globals = HeliosGlobals(_globals);
    }

    function setGlobals(address newGlobals) external {
        _isValidGovernor();
        globals = HeliosGlobals(newGlobals);
    }

    function createPool(
        uint256 lockupPeriod,
        uint256 apy,
        uint256 maxInvestmentSize,
        uint256 minInvestmentSize
    ) external whenNotPaused returns (address poolAddress) {
        _whenProtocolNotPaused();
        {
            HeliosGlobals _globals = globals;
            require(_globals.isValidPoolDelegate(msg.sender), "PF:NOT_DELEGATE");
        }

        string memory name = "Helios Pool Token";
        string memory symbol = "HLS-LP";

        Pool pool = new Pool(
            msg.sender,
            lockupPeriod,
            apy,
            maxInvestmentSize,
            minInvestmentSize,
            name,
            symbol
        );

        poolAddress = address(pool);
        pools[poolsCreated] = poolAddress;
        isPool[poolAddress] = true;
        ++poolsCreated;

        emit PoolCreated(
            poolAddress,
            msg.sender,
            name,
            symbol
        );
    }

    function setPoolFactoryAdmin(address poolFactoryAdmin, bool allowed) external {
        _isValidGovernor();
        poolFactoryAdmins[poolFactoryAdmin] = allowed;
        emit PoolFactoryAdminSet(poolFactoryAdmin, allowed);
    }

    function pause() external {
        _isValidGovernorOrPoolFactoryAdmin();
        super._pause();
    }

    function unpause() external {
        _isValidGovernorOrPoolFactoryAdmin();
        super._unpause();
    }

    function _isValidGovernor() internal view {
        require(msg.sender == globals.governor(), "PF:NOT_GOV");
    }

    function _isValidGovernorOrPoolFactoryAdmin() internal view {
